function getLuckman(){var luckmans=$(".luckman");var iLen=luckmans.length;for(var i=0;i<iLen;i++){luckmans[i].style.display="none"}var show=getNRandom(5,0,iLen);var jLen=show.length;for(var j=0;j<jLen;j++){if(show[j]!=undefined){luckmans[show[j]].style.display="block"}}}var timeout=false;function time(){if(!timeout){return}getLuckman();setTimeout(time,100)}function start(){timeout=true;time()}function end(){timeout=false}function getNRandom(n,min,max){var arr=[];for(var i=0;i<n;i++){arr[i]=parseInt(Math.random()*(max-min)+min);for(var j=0;j<i;j++){if(arr[i]==arr[j]){i=i-1;break}}}return arr}