window.onload=function(){function $(id){return document.getElementById(id)}var menu=$("topTags").getElementsByTagName("ul")[0];var tags=menu.getElementsByTagName("li");var ck=$("leftMenu").getElementsByTagName("ul")[0].getElementsByTagName("li");var j;for(i=0;i<ck.length;i++){ck[i].onclick=function(){$("welcome").style.display="none";for(j=0;j<8;j++){if(this==ck[j]){if($("p"+j)==null){openNew(j,this.innerHTML)}clearStyle();$("p"+j).style.backgroundColor="yellow";clearContent();$("c"+j).style.display="block"}}return false}}function openNew(id,name){var tagMenu=document.createElement("li");tagMenu.id="p"+id;tagMenu.innerHTML=name+"   "+"<img src='http://www.tjdadi.com.cn/off.gif' style='vertical-align:middle'/>";tagMenu.onclick=function(evt){clearStyle();tagMenu.style.backgroundColor="yellow";clearContent();$("c"+id).style.display="block"};tagMenu.getElementsByTagName("img")[0].onclick=function(evt){evt=evt?evt:window.event?window.event:null;if(evt.stopPropagation){evt.stopPropagation()}this.parentNode.parentNode.removeChild(tagMenu);var color=tagMenu.style.backgroundColor;if(color=="#ffff00"||color=="yellow"){if(tags.length-1>=0){clearStyle();tags[tags.length-1].style.backgroundColor="yellow";clearContent();var cc=tags[tags.length-1].id.split("p");$("c"+cc[1]).style.display="block"}else{clearContent();$("welcome").style.display="block"}}};menu.appendChild(tagMenu)}function clearStyle(){for(i=0;i<tags.length;i++){menu.getElementsByTagName("li")[i].style.backgroundColor="#FFFFFF"}}function clearContent(){for(i=0;i<7;i++){$("c"+i).style.display="none"}}};